                {
                    name: `ðŸ“˜â”†Message type`,
                    value: `${type}`,
                    inline: true
                },
                {
                    name: `ðŸ’¬â”†Message`,
                    value: `${message}`,
                    inline: true
                },
            ],
            type: 'editreply'
        }, interaction)
    }
}

