                if (nextY < 0) {
                    nextY = height - 1;
                }
                nextPos.y = nextY;
            } else if (btn.customId === id4) {
                let nextY = snakeHead.y + 1;
                if (nextY >= height) {
                    nextY = 0;
                }
                nextPos.y = nextY;
            } else if (btn.customId === id5) {
                let nextX = snakeHead.x + 1;
                if (nextX >= width) {
                    nextX = 0;
                }
                nextPos.x = nextX;
            } else if (btn.customId === id6) {
                gameOver(m);
                collector.stop();
            }

            if (isLocInSnake(nextPos)) {
                gameOver(m);
                collector.stop();
            } else {
                snake.unshift(nextPos);
                if (snake.length > snakeLength) {
                    snake.pop();
                }
                step(m);
            }
        });
    })
}

