              name: "ðŸ˜œâ”‡Emoji name",
              value: `${emoji.name}`,
              inline: true,
            },
            {
              name: "ðŸ˜â”‡Emoji id",
              value: `${emoji.id}`,
              inline: true,
            },
          ],
          type: 'editreply'
        }, interaction)
      })
    }else{
      interaction.guild.emojis.create({ attachment: url, name: parsedEmoji.name }).then(emoji => {
        client.succNormal({
          text: `Emoji successfully added to the server`,
          fields: [
            {
              name: "ðŸ˜›â”‡Emoji",
              value: `${emoji}`,
              inline: true,
            },
            {
              name: "ðŸ˜œâ”‡Emoji name",
              value: `${emoji.name}`,
              inline: true,
            },
            {
              name: "ðŸ˜â”‡Emoji id",
              value: `${emoji.id}`,
              inline: true,
