    user.timeout(time * 60 * 1000, reason).then(m => {
        client.succNormal({
            text: `${user} successfully timed out **${time} minutes**`,
            fields: [
                {
                    name: `ðŸ’¬â”†Reason`,
                    value: `${reason}`
                }
            ],
            type: 'editreply'
        }, interaction)
    }).catch(e => {
        client.errNormal({
            error: `I can't timeout ${user.tag}`,
            type: 'editreply'
        }, interaction);
    })
}

