src/commands/stickymessages/messages.js.content:
const Discord = require('discord.js');

const Schema = require("../../database/models/stickymessages");

module.exports = async (client, interaction, args) => {
    const data = await Schema.find({ Guild: interaction.guild.id });

    if (data) {
        let list = ``;

        for (var i = 0; i < data.length; i++) {
            list += `**${i + 1}** - Channel: ${data[i].Channel}`;
        }

        await client.embed({ 
            title: `ðŸ’¬ãƒ»Sticky messages`, 
            desc: list, 
            type: 'editreply' 
        }, interaction)
    }
    else {
        client.errNormal({ 
            error: "No data found!",
            type: 'editreply' 
        }, interaction)
    }
}

src/commands/stickymessages/stick.js.content:
const Discord = require('discord.js');

const Schema = require("../../database/models/stickymessages");

module.exports = async (client, interaction, args) => {
    const channel = interaction.options.getChannel('channel');
    const content = interaction.options.getString('message');

    const embed = new Discord.EmbedBuilder()
        .setDescription(`${content}`)
        .setColor(client.config.colors.normal);
    channel.send({ embeds: [embed] }).then(msg => {
        Schema.findOne({ Guild: interaction.guild.id, Channel: channel.id }, async (err, data) => {
            if (data) {
                data.Channel = channel.id;
                data.Content = content;
                data.LastMessage = msg.id;
                data.save();
            }
            else {
                new Schema({
                    Guild: interaction.guild.id,
                    Channel: channel.id,
                    LastMessage: msg.id,
                    Content: content,
                }).save();
            }
        })

        client.succNormal({
            text: "Sticky message created",
            fields: [
                {
                    name: `ðŸ’¬â”†Message`,
                    value: `${content}`
                }
            ],
            type: 'editreply'
        }, interaction)
    })
}

src/commands/stickymessages/unstick.js.content:
const Discord = require('discord.js');

const Schema = require("../../database/models/stickymessages");

module.exports = async (client, interaction, args) => {
    const channel = interaction.options.getChannel('channel');

    Schema.findOne({ Guild: interaction.guild.id, Channel: channel.id }, async (err, data) => {
        if (data) {
            Schema.findOneAndDelete({ Guild: interaction.guild.id, Channel: channel.id }).then(() => {
                client.succNormal({
                    text: "Sticky message deleted",
                    fields: [
                        {
                            name: `ðŸ“˜â”†Channel`,
                            value: `${channel}`
                        }
                    ],
                    type: 'editreply'
                }, interaction);
            })
        }
        else {
            client.errNormal({
                error: 'No message found!',
                type: 'editreply'
            }, interaction)
        }
    })
}

src/commands/suggestions/accept.js.content:
const Discord = require('discord.js');

const Schema = require("../../database/models/suggestionChannels");

module.exports = async (client, interaction, args) => {
    const perms = await client.checkUserPerms({
        flags: [Discord.PermissionsBitField.Flags.ManageMessages],
        perms: [Discord.PermissionsBitField.Flags.ManageMessages]
    }, interaction)

    if (perms == false) return;

    const messageID = interaction.options.getString('id');

    const data = await Schema.findOne({ Guild: interaction.guild.id });
    if (data) {
        const suggestionchannel = interaction.guild.channels.cache.get(data.Channel);
        const suggestEmbed = await suggestionchannel.messages.fetch(messageID);
        const embedData = suggestEmbed.embeds[0];

        client.embed({
            title: `${client.emotes.normal.check}ãƒ»Suggestion accepted`,
            desc: `\`\`\`${embedData.description}\`\`\``,
            color: client.config.colors.succes,
            author: {
                name: embedData.author.name,
                iconURL: embedData.author.iconURL
            },
            type: 'edit'
        }, suggestEmbed)

        try {
            const user = await client.users.cache.find((u) => u.tag === embedData.author.name);

            if (user) {
                client.embed({
                    title: `${client.emotes.normal.check}ãƒ»Suggestion accepted`,
                    desc: `Your suggestion in ${interaction.guild.name} has been accepted by a moderator!`,
                    fields: [
                        {
                            name: `ðŸ’¬â”†Suggestion`,
                            value: `${embedData.description}`
                        }
                    ],
                }, user).catch({})
            }
        }
        catch { }

        client.succNormal({
            text: "Suggestion successfully accepted",
            fields: [
                {
                    name: `ðŸ’¬â”†Suggestion`,
                    value: `${embedData.description}`
                }
            ],
            type: 'editreply'
        }, interaction);
    }
    else {
        client.errNormal({
            error: `No suggestion channel set! Please do the setup`,
            type: 'editreply'
        }, interaction);
    }
}

