src/events/invite/inviteJoin.js.content:
const discord = require('discord.js');

const invites = require("../../database/models/invites");
const invitedBy = require("../../database/models/inviteBy");
const welcomeSchema = require("../../database/models/welcomeChannels");
const messages = require("../../database/models/inviteMessages");
const rewards = require("../../database/models/inviteRewards");


module.exports = async (client, member, invite, inviter) => {
    const messageData = await messages.findOne({ Guild: member.guild.id });

    if (!invite || !inviter) {
        if (messageData && messageData.inviteJoin) {
            var joinMessage = messageData.inviteJoin;
            joinMessage = joinMessage.replace(`{user:username}`, member.user.username)
            joinMessage = joinMessage.replace(`{user:discriminator}`, member.user.discriminator)
            joinMessage = joinMessage.replace(`{user:tag}`, member.user.tag)
            joinMessage = joinMessage.replace(`{user:mention}`, member)

            joinMessage = joinMessage.replace(`{inviter:username}`, "System")
            joinMessage = joinMessage.replace(`{inviter:discriminator}`, "#0000")
            joinMessage = joinMessage.replace(`{inviter:tag}`, "System#0000")
            joinMessage = joinMessage.replace(`{inviter:mention}`, "System")
            joinMessage = joinMessage.replace(`{inviter:invites}`, "âˆž")
            joinMessage = joinMessage.replace(`{inviter:invites:left}`, "âˆž")

            joinMessage = joinMessage.replace(`{guild:name}`, member.guild.name)
            joinMessage = joinMessage.replace(`{guild:members}`, member.guild.memberCount)

            welcomeSchema.findOne({ Guild: member.guild.id }, async (err, channelData) => {
                if (channelData) {

                    var channel = member.guild.channels.cache.get(channelData.Channel)

                    await client.embed({
                        title: `ðŸ‘‹ãƒ»Welcome`,
                        desc: joinMessage
                    }, channel).catch(() => { })
                }
            })
        } else {
            welcomeSchema.findOne({ Guild: member.guild.id }, async (err, channelData) => {
                if (channelData) {

                    var channel = member.guild.channels.cache.get(channelData.Channel)

                    client.embed({
                        title: `ðŸ‘‹ãƒ»Welcome`,
                        desc: `I cannot trace how **${member} | ${member.user.tag}** has been joined`
                    }, channel).catch(() => { })
                }
            })
        }
    }
    else {
        const data = await invites.findOne({ Guild: member.guild.id, User: inviter.id });

        if (data) {
            data.Invites += 1;
            data.Total += 1;
            data.save();

            if (messageData) {
                var joinMessage = messageData.inviteJoin;
                joinMessage = joinMessage.replace(`{user:username}`, member.user.username)
                joinMessage = joinMessage.replace(`{user:discriminator}`, member.user.discriminator)
                joinMessage = joinMessage.replace(`{user:tag}`, member.user.tag)
                joinMessage = joinMessage.replace(`{user:mention}`, member)

                joinMessage = joinMessage.replace(`{inviter:username}`, inviter.username)
                joinMessage = joinMessage.replace(`{inviter:discriminator}`, inviter.discriminator)
                joinMessage = joinMessage.replace(`{inviter:tag}`, inviter.tag)
                joinMessage = joinMessage.replace(`{inviter:mention}`, inviter)
                joinMessage = joinMessage.replace(`{inviter:invites}`, data.Invites)
                joinMessage = joinMessage.replace(`{inviter:invites:left}`, data.Left)

                joinMessage = joinMessage.replace(`{guild:name}`, member.guild.name)
                joinMessage = joinMessage.replace(`{guild:members}`, member.guild.memberCount)

                welcomeSchema.findOne({ Guild: member.guild.id }, async (err, channelData) => {
                    if (channelData) {

                        var channel = member.guild.channels.cache.get(channelData.Channel)

                        await client.embed({
                            title: `ðŸ‘‹ãƒ»Welcome`,
                            desc: joinMessage
                        }, channel).catch(() => { })
                    }
                })
            }
            else {
                welcomeSchema.findOne({ Guild: member.guild.id }, async (err, channelData) => {
                    if (channelData) {

                        var channel = member.guild.channels.cache.get(channelData.Channel)

                        client.embed({
                            title: `ðŸ‘‹ãƒ»Welcome`,
                            desc: `**${member} | ${member.user.tag}** was invited by ${inviter.tag} **(${data.Invites} invites)**`
                        }, channel)
                    }
                })
            }

            rewards.findOne({ Guild: member.guild.id, Invites: data.Invites }, async (err, data) => {
                if (data) {
                    try {
                        var role = member.guild.roles.cache.get(data.Role);
                        member.roles.add(role);
                    }
                    catch { }
                }
            })
        }
        else {
            new invites({
                Guild: member.guild.id,
                User: inviter.id,
                Invites: 1,
                Total: 1,
                Left: 0
            }).save();

            if (messageData) {
                var joinMessage = messageData.inviteJoin;
                joinMessage = joinMessage.replace(`{user:username}`, member.user.username)
                joinMessage = joinMessage.replace(`{user:discriminator}`, member.user.discriminator)
                joinMessage = joinMessage.replace(`{user:tag}`, member.user.tag)
                joinMessage = joinMessage.replace(`{user:mention}`, member)

                joinMessage = joinMessage.replace(`{inviter:username}`, inviter.username)
                joinMessage = joinMessage.replace(`{inviter:discriminator}`, inviter.discriminator)
                joinMessage = joinMessage.replace(`{inviter:tag}`, inviter.tag)
                joinMessage = joinMessage.replace(`{inviter:mention}`, inviter)
                joinMessage = joinMessage.replace(`{inviter:invites}`, "1")
                joinMessage = joinMessage.replace(`{inviter:invites:left}`, "0")

                joinMessage = joinMessage.replace(`{guild:name}`, member.guild.name)
                joinMessage = joinMessage.replace(`{guild:members}`, member.guild.memberCount)

                welcomeSchema.findOne({ Guild: member.guild.id }, async (err, channelData) => {
                    if (channelData) {

                        var channel = member.guild.channels.cache.get(channelData.Channel)

                        await client.embed({
                            title: `ðŸ‘‹ãƒ»Welcome`,
                            desc: joinMessage
                        }, channel).catch(() => { })
                    }
                })
            }
            else {
                welcomeSchema.findOne({ Guild: member.guild.id }, async (err, channelData) => {
                    if (channelData) {

                        var channel = member.guild.channels.cache.get(channelData.Channel)

                        await client.embed({
                            title: `ðŸ‘‹ãƒ»Welcome`,
                            desc: `**${member} | ${member.user.tag}** was invited by ${inviter.tag} **(1 invites)**`
                        }, channel).catch(() => { })
                    }
                })
            }
        }

        invitedBy.findOne({ Guild: member.guild.id }, async (err, data2) => {
            if (data2) {
                data2.inviteUser = inviter.id,
                    data2.User = member.id
                data2.save();
            }
            else {
                new invitedBy({
                    Guild: member.guild.id,
                    inviteUser: inviter.id,
                    User: member.id
                }).save();
            }
        })
    }
};

src/events/message/messageCreate.js.content:
const Discord = require("discord.js");

const Functions = require("../../database/models/functions");
const afk = require("../../database/models/afk");
const chatBotSchema = require("../../database/models/chatbot-channel");
const messagesSchema = require("../../database/models/messages");
const messageSchema = require("../../database/models/levelMessages");
const messageRewards = require("../../database/models/messageRewards");
const Schema = require("../../database/models/stickymessages");
const levelRewards = require("../../database/models/levelRewards");
const levelLogs = require("../../database/models/levelChannels");
const Commands = require("../../database/models/customCommand");
const CommandsSchema = require("../../database/models/customCommandAdvanced");
const fetch = require("node-fetch");

/**
 * 
 * @param {Discord.Client} client 
 * @param {Discord.Message} message 
 * @returns 
 */
module.exports = async (client, message) => {
  const dmlog = new Discord.WebhookClient({
    id: client.webhooks.dmLogs.id,
    token: client.webhooks.dmLogs.token,
  });

  if (message.author.bot) return;

  if (message.channel.type === Discord.ChannelType.DM) {
    let embedLogs = new Discord.EmbedBuilder()
      .setTitle(`ðŸ’¬ãƒ»New DM message!`)
      .setDescription(`Bot has received a new DM message!`)
      .addFields(
        { name: "ðŸ‘¤â”†Send By", value: `${message.author} (${message.author.tag})`, inline: true },
        { name: `ðŸ’¬â”†Message`, value: `${message.content || "None"}`, inline: true },
      )
      .setColor(client.config.colors.normal)
      .setTimestamp();

    if (message.attachments.size > 0)
      embedLogs.addFields(
        { name: `ðŸ“ƒâ”†Attachments`, value: `${message.attachments.first()?.url}`, inline: false },
      )
    return dmlog.send({
      username: "Bot DM",
      embeds: [embedLogs],
    });
  }

  // Levels
  Functions.findOne({ Guild: message.guild.id }, async (err, data) => {
    if (data) {
      if (data.Levels == true) {
        const randomXP = Math.floor(Math.random() * 9) + 1;
        const hasLeveledUp = await client.addXP(
          message.author.id,
          message.guild.id,
          randomXP
        );

        if (hasLeveledUp) {
          const user = await client.fetchLevels(
            message.author.id,
            message.guild.id
          );

          const levelData = await levelLogs.findOne({
            Guild: message.guild.id,
          });
          const messageData = await messageSchema.findOne({
            Guild: message.guild.id,
          });

          if (messageData) {
            var levelMessage = messageData.Message;
            levelMessage = levelMessage.replace(
              `{user:username}`,
              message.author.username
            );
            levelMessage = levelMessage.replace(
              `{user:discriminator}`,
              message.author.discriminator
            );
            levelMessage = levelMessage.replace(
              `{user:tag}`,
              message.author.tag
            );
            levelMessage = levelMessage.replace(
              `{user:mention}`,
              message.author
            );

            levelMessage = levelMessage.replace(`{user:level}`, user.level);
            levelMessage = levelMessage.replace(`{user:xp}`, user.xp);

            try {
              if (levelData) {
                await client.channels.cache
                  .get(levelData.Channel)
                  .send({ content: levelMessage })
                  .catch(() => { });
              } else {
                await message.channel.send({ content: levelMessage });
              }
            } catch {
              await message.channel.send({ content: levelMessage });
            }
          } else {
            try {
              if (levelData) {
                await client.channels.cache
                  .get(levelData.Channel)
                  .send({
                    content: `**GG** <@!${message.author.id}>, you are now level **${user.level}**`,
                  })
                  .catch(() => { });
              } else {
                message.channel.send({
                  content: `**GG** <@!${message.author.id}>, you are now level **${user.level}**`,
                });
              }
            } catch {
              message.channel.send({
                content: `**GG** <@!${message.author.id}>, you are now level **${user.level}**`,
              });
            }
          }

          levelRewards.findOne(
            { Guild: message.guild.id, Level: user.level },
            async (err, data) => {
              if (data) {
                message.guild.members.cache
                  .get(message.author.id)
                  .roles.add(data.Role)
                  .catch((e) => { });
              }
            }
          );
        }
      }
    }
  });

  // Message tracker system
  messagesSchema.findOne(
    { Guild: message.guild.id, User: message.author.id },
    async (err, data) => {
      if (data) {
        data.Messages += 1;
        data.save();

        messageRewards.findOne(
          { Guild: message.guild.id, Messages: data.Messages },
          async (err, data) => {
            if (data) {
              try {
                message.guild.members.cache
                  .get(message.author.id)
                  .roles.add(data.Role);
              } catch { }
            }
          }
        );
      } else {
        new messagesSchema({
          Guild: message.guild.id,
          User: message.author.id,
          Messages: 1,
        }).save();
      }
    }
  );

  // AFK system
  afk.findOne(
    { Guild: message.guild.id, User: message.author.id },
    async (err, data) => {
      if (data) {
        await afk.deleteOne({
          Guild: message.guild.id,
          User: message.author.id,
        });

        client
          .simpleEmbed(
            {
              desc: `${message.author} is no longer afk!`,
            },
            message.channel
          )
          .then(async (m) => {
            setTimeout(() => {
              m.delete();
            }, 5000);
          });

        if (message.member.displayName.startsWith(`[AFK] `)) {
          let name = message.member.displayName.replace(`[AFK] `, ``);
          message.member.setNickname(name).catch((e) => { });
        }
      }
    }
  );

  message.mentions.users.forEach(async (u) => {
    if (
      !message.content.includes("@here") &&
      !message.content.includes("@everyone")
    ) {
      afk.findOne(
        { Guild: message.guild.id, User: u.id },
        async (err, data) => {
          if (data) {
            client.simpleEmbed(
              { desc: `${u} is currently afk! **Reason:** ${data.Message}` },
              message.channel
            );
          }
        }
      );
    }
  });

  // Chat bot
  chatBotSchema.findOne({ Guild: message.guild.id }, async (err, data) => {
    if (!data) return;
    if (message.channel.id !== data.Channel) return;
    if (process.env.OPENAI) {
      fetch(
        `https://api.openai.com/v1/completions`,
        {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': 'Bearer ' + process.env.OPENAI,
          },
          // body: '{\n  "model": "text-davinci-003",\n  "prompt": "What is your name?",\n  "max_tokens": 4000,\n  "temperature": 0\n}',
          body: JSON.stringify({
            'model': 'text-davinci-003',
            'prompt': message.content,
            'temperature': 0,
            'max_tokens': 256,
            'top_p': 1,
            'frequency_penalty': 0,
            'presence_penalty': 0,
          })
        }
      )
        .catch(() => {
        })
        .then((res) => {
          res.json().then((data) => {
            if(data.error) return console.log(data.error);
            message.reply({ content: data.choices[0].text });
          });
        });
    } else {
      try {
        const input = message;
        try {
          fetch(
            `https://api.coreware.nl/fun/chat?msg=${encodeURIComponent(input)}&uid=${message.author.id}`,
          )
            .catch(() => { console.log })
            .then((res) => res.json())
            .catch(() => { console.log})
            .then(async (json) => {
              console.log(json);
              if (json) {
                if (
                  json.response !== " " ||
                  json.response !== undefined ||
                  json.response !== "" ||
                  json.response !== null
                ) {
                  try {
                    return message
                      .reply({ content: json.response })
                      .catch(() => { });
                  } catch { }
                }
              }
            })
            .catch(() => { });
        } catch { }
      } catch { }
    }
  });

  // Sticky messages
  try {
    Schema.findOne(
      { Guild: message.guild.id, Channel: message.channel.id },
      async (err, data) => {
        if (!data) return;

        const lastStickyMessage = await message.channel.messages
          .fetch(data.LastMessage)
          .catch(() => { });
        if (!lastStickyMessage) return;
        await lastStickyMessage.delete({ timeout: 1000 });

        const newMessage = await client.simpleEmbed(
          { desc: `${data.Content}` },
          message.channel
        );

        data.LastMessage = newMessage.id;
        data.save();
      }
    );
  } catch { }

  // Prefix
  var guildSettings = await Functions.findOne({ Guild: message.guild.id });
  if (!guildSettings) {
    new Functions({
      Guild: message.guild.id,
      Prefix: client.config.discord.prefix,
    }).save();

    guildSettings = await Functions.findOne({ Guild: message.guild.id });
  }

  if (!guildSettings || !guildSettings.Prefix) {
    Functions.findOne({ Guild: message.guild.id }, async (err, data) => {
      data.Prefix = client.config.discord.prefix;
      data.save();
    });

    guildSettings = await Functions.findOne({ Guild: message.guild.id });
  }

  if (!guildSettings || !guildSettings.Prefix) {
    var prefix = client.config.Discord.prefix;
  } else {
    var prefix = guildSettings.Prefix;
  }

  const escapeRegex = (str) => str.replace(/[.*+?^${}()|[\]\\]/g, "\\$&");
  const prefixRegex = new RegExp(
    `^(<@!?${client.user.id}>|${escapeRegex(prefix)})\\s*`
  );

  if (!prefixRegex.test(message.content.toLowerCase())) return;
  const [, matchedPrefix] = message.content.toLowerCase().match(prefixRegex);

  const args = message.content.slice(matchedPrefix.length).trim().split(/ +/g);
  const command = args.shift().toLowerCase();

  if (
    message.mentions.users.first() &&
    message.mentions.users.first().id == client.user.id &&
    command.length === 0
  ) {
    let row = new Discord.ActionRowBuilder().addComponents(
      new Discord.ButtonBuilder()
        .setLabel("Invite")
        .setURL(
          client.config.discord.botInvite
        )
        .setStyle(Discord.ButtonStyle.Link),

      new Discord.ButtonBuilder()
        .setLabel("Support server")
        .setURL(client.config.discord.serverInvite)
        .setStyle(Discord.ButtonStyle.Link)
    );

    client
      .embed(
        {
          title: "Hi, i'm Bot",
          desc: `Use with commands via Discord ${client.emotes.normal.slash} commands`,
          fields: [
            {
              name: "ðŸ“¨â”†Invite me",
              value: `Invite Bot in your own server! [Click here](${client.config.discord.botInvite})`,
            },
            {
              name: "â“â”‡I don't see any slash commands",
              value:
                "The bot may not have permissions for this. Open the invite link again and select your server. The bot then gets the correct permissions",
            },
            {
              name: "â“â”†Need support?",
              value: `For questions you can join our [support server](${client.config.discord.serverInvite})!`,
            },
            {
              name: "ðŸžâ”†Found a bug?",
              value: `Report all bugs via: \`/report bug\`!`,
            },
          ],
          components: [row],
        },
        message.channel
      )
      .catch(() => { });
  }

  const cmd = await Commands.findOne({
    Guild: message.guild.id,
    Name: command,
  });
  if (cmd) {
    return message.channel.send({ content: cmdx.Responce });
  }

  const cmdx = await CommandsSchema.findOne({
    Guild: message.guild.id,
    Name: command,
  });
  if (cmdx) {
    if (cmdx.Action == "Normal") {
      return message.channel.send({ content: cmdx.Responce });
    } else if (cmdx.Action == "Embed") {
      return client.simpleEmbed(
        {
          desc: `${cmdx.Responce}`,
        },
        message.channel
      );
    } else if (cmdx.Action == "DM") {
      return message.author.send({ content: cmdx.Responce }).catch((e) => {
        client.errNormal(
          {
            error: "I can't DM you, maybe you have DM turned off!",
          },
          message.channel
        );
      });
    }
  }
};

