      const image = reaction.message.attachments.size > 0 ? await extension(reaction, reaction.message.attachments.first()?.url) : "";
      const starMsg = await starboardChannel.messages.fetch(stars.id);

      if (reaction.count <= 0) {
        starMsg.delete();
      }
      else {
